import React, { useState, useEffect } from 'react'

export const Barrier = ({ number, remove, P, setP, withH, H, checked, setChecked }) => {
    const [um, setUm] = useState(0)
    const [fm, setFm] = useState(0)

    useEffect(() => {
        if (um === 0 || fm === 0 || (withH && H === 0)) {
            setP(NaN)
        } else {
            const result = withH
                ? (1 / um) / (1 / um + 1 / fm + 1 / H)
                : (1 / um) / (1 / um + 1 / fm)
            setP(result)
        }
    }, [H, withH, um, fm])

    return (
        <div className='Barrier'>
            <input
                type='checkbox'
                onChange={({ target }) => {
                    setChecked(target.checked)
                }}
                disabled={number === 1}
                checked={checked}
            />
            <span>{`Преграда ${number}`}</span>
            <span>um</span>
            <input
                type='number'
                min={0}
                value={um}
                onChange={({ target }) => {
                    const value = parseFloat(target.value)
                    setUm(value)
                }} />
            <span>fm</span>
            <input
                type='number'
                min={0}
                value={fm}
                onChange={({ target }) => {
                    const value = parseFloat(target.value)
                    setFm(value)
                }}
            />
            <span>{`Pнсд = ${P || 'Деление на ноль'}`}</span>
            <span>{`Pзащ = ${1 - P || 'Деление на ноль'}`}</span>
            {number !== 1 && <button onClick={remove}>Удалить</button>}
        </div>
    )
}
