import { useState } from 'react';
import './App.css';

function App() {
  const [todos, setTodos] = useState([
    {
      id: 1,
      text: "todo1",
    },
    {
      id: 2,
      text: "todo2",
    },
    {
      id: 3,
      text: "todo3",
    },
  ]);

  const [newTodo, setNewTodo] = useState({
    id: todos.length + 1,
    text: "",
  });

  const onCreateTodoClickHandler = () => {
    setTodos(prev => ([
      ...prev,
      newTodo,
    ]))
    setNewTodo({
      id: todos.length + 1,
      text: "",
    })
  }

  if (!todos.length) {
    return <p>Loading...</p>
  }

  return (
    <div className="App">
      <h1>Todos</h1>
      <input type="text" value={newTodo.text} onChange={(event) => setNewTodo({
        id: todos.length + 1,
        text: event.target.value,
      })} placeholder="Write todo name" />
      <button onClick={onCreateTodoClickHandler}>Create todo</button>
      <ul>
        {
          todos.map(todo => {
            return (
              <li key={"todo - " + todo.id}>
                <span>&#10003;</span>
                <p style={{
                  display: "inline-block",
                }}>{todo.text}</p>
                <span>&#10060;</span>
              </li>
            )
          })
        }
      </ul>
    </div>
  );
}

export default App;
