import { useState } from 'react';
import './App.css';

function App() {
  const [todos, setTodos] = useState([]);

  const [newTodo, setNewTodo] = useState({
    id: todos.length + 1,
    text: "",
    done: false,
  });

  const onCreateTodoClickHandler = () => {
    setTodos(prev => ([
      ...prev,
      newTodo,
    ]))
    setNewTodo({
      id: todos.length + 1,
      text: "",
      done: false,
    })
  }

  const onDoneClickHandler = (todoId) => {
    setTodos(prev => prev.map(todo => {
      if (todo.id === todoId) {
        return {
          ...todo,
          done: !todo.done,
        }
      }
      return todo;
    }))
  }

  const onDeleteClickHandler = (todoId) => {
    setTodos(prev => prev.filter(todo => todo.id !== todoId));
  }

  return (
    <div className="App">
      <h1>Todos</h1>
      <input type="text" value={newTodo.text} onChange={(event) => setNewTodo({
        id: todos.length + 1,
        text: event.target.value,
        done: false,
      })} placeholder="Write todo name" />
      <button onClick={onCreateTodoClickHandler}>Create todo</button>
      <ul>
        {
          todos.map(todo => {
            return (
              <li key={"todo - " + todo.id} className={todo.done ? "todo_done" : null}>
                <input type="checkbox" checked={todo.done} onChange={() => onDoneClickHandler(todo.id)} />
                <p style={{
                  display: "inline-block",
                }}>{todo.text}</p>
                <span onClick={() => onDeleteClickHandler(todo.id)}>&#10060;</span>
              </li>
            )
          })
        }
      </ul>
    </div>
  );
}

export default App;
